mapper.select:
select , u.id u_id, u.status u_status, u.username u_username, u.avatar u_avatar
from post p, user u
where u.id=p.user_id and p.id=#{postId}

select 
from post


    id = #{id}


    and created_at = #{createdAt}


    and updated_at = #{updatedAt}


    and deleted = #{deleted}


    and content = #{content}


    and user_id = #{userId}


    and topic_id = #{topicId}


    and draft = #{draft}


    and target_id = #{targetId}


order by created_at desc
