mapper.insert:
insert into topic (id, created_at, updated_at, 
  deleted, title, content, 
  user_id, section_id, draft
  )
values (#{id,jdbcType=INTEGER}, #{createdAt,jdbcType=TIMESTAMP}, #{updatedAt,jdbcType=TIMESTAMP}, 
  #{deleted,jdbcType=BIT}, #{title,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR},
  #{userId,jdbcType=INTEGER}, #{sectionId,jdbcType=INTEGER}, #{draft,jdbcType=BIT}
  )

mapper.select:
select , u.id u_id, u.status u_status, u.username u_username, u.avatar u_avatar, s.id s_id, s.name s_name
from topic t, user u, section s

  u.id=t.user_id and s.id=t.section_id

    and t.user_id=#{userId}


    and t.section_id=#{sectionId}


    and t.draft=#{draft}


    and t.deleted=#{deleted}


order by created_at desc
