mapper.insert:
insert into users_sections (id, created_at, updated_at, 
  deleted, user_id, section_id
  )
values (#{id,jdbcType=BIGINT}, #{createdAt,jdbcType=TIMESTAMP}, #{updatedAt,jdbcType=TIMESTAMP},
  #{deleted,jdbcType=BIT}, #{userId,jdbcType=BIGINT}, #{sectionId,jdbcType=BIGINT}
  )

mapper.select:
select  from users_sections


    id=#{id}


    and deleted=#{deleted}


    and user_id=#{userId}


    and section_id=#{sectionId}
